// Get columns object from inference output
const cols = $json["columns"];

// Convert it into an array of {column, type}, plus add a link field
const results = Object.entries(cols).map(([name, dtype]) => {
  return {
    column: name,
    inferred_type: dtype,
    ui_link: "http://localhost:8501/"
  };
});

return results;

// Loop over input items and add a new field with the Streamlit URL
for (const item of $input.all()) {
  item.json.ui_link = "http://localhost:8501/";
}

return $input.all();

const csvText = $json["data"];  
const rows = csvText.trim().split("\n");
const headers = rows[0].split(",");

const results = rows.slice(1).map(r => {
  const values = r.split(",");
  let obj = {}; 

  headers.forEach((h, i) => {
    obj[h.trim()] = values[i] ? values[i].trim() : null;
  });

  obj["ui_link"] = "http://localhost:8501/";
  return obj;
});

return results;

